<div class="modal fade text-left modal-borderless" id="default" tabindex="-1" role="dialog" aria-labelledby="myModalLabel1" aria-hidden="true">
    <div class="modal-dialog modal-dialog-scrollable modal-lg" role="document">
        <div class="modal-content">
            <div class="modal-header">
                
             <a href="#" title="Principal" class="btn btn-sm btn-outline-secondary" 
   data-bs-toggle="modal" data-bs-target="#default">
    <i data-feather="user" width="40"></i>        
    Registrar Usuarios 
</a>
                <button type="button" class="close rounded-pill" data-bs-dismiss="modal" aria-label="Close">
                    <i data-feather="x"></i>
                </button>
            </div>

            <div class="modal-body">
                <form class="form">
                    <div class="row">
                        <div class="col-md-6 col-12">
                            <div class="form-group">
                                <label for="first-name-column">First Name</label>
                                <input type="text" id="first-name-column" class="form-control" placeholder="First Name">
                            </div>
                        </div>
                        <div class="col-md-6 col-12">
                            <div class="form-group">
                                <label for="last-name-column">Last Name</label>
                                <input type="text" id="last-name-column" class="form-control" placeholder="Last Name">
                            </div>
                        </div>

                        <div class="col-md-6 col-12">
                            <div class="form-group">
                                <label for="city-column">City</label>
                                <input type="text" id="city-column" class="form-control" placeholder="City">
                            </div>
                        </div>
                        <div class="col-md-6 col-12">
                            <div class="form-group">
                                <label for="country-floating">Country</label>
                                <input type="text" id="country-floating" class="form-control" placeholder="Country">
                            </div>
                        </div>

                        <div class="col-md-6 col-12">
                            <div class="form-group">
                                <label for="company-column">Company</label>
                                <input type="text" id="company-column" class="form-control" placeholder="Company">
                            </div>
                        </div>
                        <div class="col-md-6 col-12">
                            <div class="form-group">
                                <label for="email-id-column">Email</label>
                                <input type="email" id="email-id-column" class="form-control" placeholder="Email">
                            </div>
                        </div>
                    </div>
                </form>
            </div>

            <div class="modal-footer">
                <button type="button" class="btn" data-bs-dismiss="modal">Cerrar</button>
                <button type="button" class="btn btn-secondary">Registrar</button>
            </div>
        </div>
    </div>
</div>
